import React from 'react';
//import logo from './assets/logo.png';

//our inspiration: https://www.w3schools.com/howto/howto_css_dropdown.asp

function App() {
  return (
    <div>
      <Navbar/>
      <h1>CantoLTA</h1>
    </div>
  );
}

function Navbar() {
  return (
    <div className='navbar'>
      <NavItemSingle path='#'>TrialS</NavItemSingle>
      <NavItemDrop path='#'>TrialD</NavItemDrop>
    </div>
  );
}

function NavItemSingle(props) {
  return (
    <div className='nav-item-single'>
      <a href={props.path}>{props.children}</a>
    </div>
  );
}

function NavItemDrop(props) {
  return (
    <div className='nav-item-drop'>
      <button className='dropbtn'
        onClick="window.location.href = {props.path};"
        >{props.children}</button>
      <div className='dropdown-content'>
        <a href='#'>element1</a>
      </div>
    </div>
  );
}

export default App;

