import React, { useState } from 'react';
import { handleMouseEnter, handleMouseLeave } from './components/eventHandlers';
//import logo from './assets/logo.png';

function App() {
  return (
    <div>
      <Navbar>
        <NavItem icon='hello'>
          <DropdownMenu/>
        </NavItem>
      </Navbar>
      <h1>CantoLTA</h1>
    </div>
  );
}

function Navbar(props) {
  return (
    <nav className='navbar'>
      <ul className='navbar-nav'> { props.children } </ul>
    </nav>
  )
}

function NavItem(props) {
  const [open, setOpen] = useState(false);

  return (
    <li className='nav-item'>
      <a href="#" className='icon-button'
        //onClick={() => setOpen(!open)}>
        onMouseEnter={() => handleMouseEnter(setOpen)}
        onMouseLeave={() => handleMouseLeave(setOpen)}>
        {props.icon}
      </a>
      {open && props.children}
    </li>
  )
}

function DropdownMenu() {

  function DropdownItem(props) {
    return (
      <a href='#' className='menu-item'>
        <span className='icon-button'>{props.leftIcon}</span>
        {props.children}
        <span className='icon-right'>{props.rightIcon}</span>
      </a>
    )
  }

  const [menuOpen, setMenuOpen] = useState();

  return (
    <div className='dropdown'
      onMouseEnter={() => setMenuOpen(true)}
      onMouseLeave={() => setMenuOpen(false)}>
      {menuOpen && (
        <div>
          <DropdownItem>hi there</DropdownItem>
          <DropdownItem>foo</DropdownItem>
          <DropdownItem>bar</DropdownItem>
          <DropdownItem>baz</DropdownItem>
        </div>
      )}
    </div>
  )
}

export default App;

